<?phpif ( ! defined( 'ABSPATH' ) ) exit;class InfiniteWoocommerceScrollSettings {    private $dir;	private $file;	private $assets_dir;	private $assets_url;	private $settings_base;		public function __construct( $file ) {		$this->file = $file;		$this->dir = dirname( $this->file );		$this->assets_dir = trailingslashit( $this->dir ) . 'include';		$this->assets_url = esc_url( trailingslashit( plugins_url( '/include/', $this->file ) ) );		$this->settings_base = 'isw_';	}}